import React from 'react';
import { View, Text, StyleSheet, ActivityIndicator } from "react-native";
import { useState, useEffect } from "react"
import { getSinglePlant } from './utils/Api';
import { Image } from 'react-native-elements';


const SingleLookedUpPlantScreen = (props:any) => {
  const {route} = props
  const [singlePlant, setSinglePlant] = useState({});
  const {commonName} = props.route.params

  const imageSource = singlePlant.image_url
  console.log(imageSource)

  useEffect(() => {
    getSinglePlant(commonName)
      .then((response) => {
        console.log(response, '<---response')
        setSinglePlant(response);

      })
      .catch((err) => {
        console.log(err, "<-----err");
      });
  },[]);

    return (
        <View style={ styles.container}>
        <Image  source={ {uri:imageSource} }  style={{ width: 300, height: 300, borderRadius:100 }}  PlaceholderContent={<ActivityIndicator />}/>
        <Text style={styles.title}> {singlePlant.commonName} </Text>
        <Text> {singlePlant.botanicalName} </Text>
        <Text> {singlePlant.description} </Text>
      </View>
    );
}

const styles = StyleSheet.create({
  container: {
    flex:1,
    alignItems: 'center',

  },
  header: {
    flex: 1,

  },
  section: {
    flex: 3,

  },
  title: {
    fontSize: 30,
    fontWeight: 400,
  }
})

export default SingleLookedUpPlantScreen
