import React from "react";
import { View, Text, ScrollView, StyleSheet, List, SafeAreaView } from "react-native";
import { useState, useEffect } from "react";
import { getPlants } from "./utils/Api";
import { ListItem, Avatar } from "react-native-elements";
import { FlatList } from "react-native-gesture-handler";

const SingleCategoryPlantScreen = (props: any) => {
  const [plants, setPlants] = useState([]);
  const {plantCategoryId} = props.route.params
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    getPlants(plantCategoryId)
      .then((response) => {
        setPlants(response);
      })
      .catch((err) => {
        console.log(err, "<-----err");
      });
  },[]);

  const renderItem = ({ item }) => {
    const backgroundColor = item.id === selectedId ? "#6e3b6e" : "#f9c2ff";
    const color = item.id === selectedId ? 'white' : 'black';

    return (
      <Item
        item={item}
        onPress={() => setSelectedId(item.botanicalName)}
        backgroundColor={{ backgroundColor }}
        textColor={{ color }}
      />
    );
  };

  return (

      <SafeAreaView style={styles.container}>
      <FlatList
        data={plants}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
        extraData={selectedId}
      />
    </SafeAreaView>


  );
};

const styles = StyleSheet.create({
  subtitleView: {

    flexDirection: "column", paddingLeft: 10, paddingTop: 5 },
  ratingImage: { height: 19.21, width: 100 },
  ratingText: { paddingLeft: 10, color: "grey" },
  scrollView: {
    backgroundColor: 'pink',
    marginHorizontal: 20,
  },
});

export default SingleCategoryPlantScreen;
